<div id="fixed-left" uk-toggle="target: #chat-room; animation: uk-animation-fade">
    <!-- <div class="uk-box-shadow-medium uk-padding-small flat-jelly uk-light uk-border-pill"> -->
        <a class="uk-box-shadow-medium uk-padding-small flat-jelly uk-light uk-border-pill" uk-icon="icon: comment"></a>
    <!-- </div> -->
</div>

<script>
    function updateScroll(){
        var element = document.getElementById("chat-flow");
        element.scrollTop = element.scrollHeight;
    }

    // Enable pusher logging - don't include this in production
    Pusher.logToConsole = true;
    var unique_id = "{{ Auth::user()->id }}";

    var pusher = new Pusher('51256090141dd467b768', {
        cluster: 'ap1',
        forceTLS: true
    });

    var channel = pusher.subscribe('my-channel');
    channel.bind('my-event', function(data) {
        //alert(JSON.stringify(data));
        document.getElementById('chat-flow').innerHTML += '<div class="uk-border-rounded messages-holder"><img src="https://pbs.twimg.com/profile_images/453956388851445761/8BKnRUXg.png" alt="" class="chat-avatar uk-border-pill" style="width: 25px; margin-left: 10px"><span class="uk-text-meta">' + data['name'] + '</span><span class="uk-text-meta uk-float-left">'+ data['date'] +'</span><p class="message-content">' + data['message'] + '</p></div>';
        beep();
        updateScroll();
    });
</script>


<script>
    function send() {
        UIkit.toggle('#chat-spinner').toggle()
        var id = "{{ Auth::user()->id }}";
        var xhttp = new XMLHttpRequest();
        xhttp.onreadystatechange = function() {
            if (this.readyState == 4 && this.status == 200) {
                // document.getElementById("demo").innerHTML = this.responseText;
                UIkit.toggle('#chat-spinner').toggle()
            }
        };

        var message = document.getElementById('user-message').value;
        xhttp.open("POST", "{{ route('Chat') }}", true);
        xhttp.setRequestHeader("Content-type", "application/x-www-form-urlencoded");
        xhttp.setRequestHeader('X-CSRF-TOKEN', '{{ csrf_token() }}')
        // ‘X-CSRF-TOKEN’: $(‘meta[name=”csrf-token”]’).attr(‘content’)
        xhttp.send("user-message="+message+"&id="+id+"&_token={{ csrf_token() }}");
        var message = document.getElementById('user-message').value = '';
    }

    function beep() {
        var snd = new Audio("data:audio/ogg;base64,");
        snd.play();
    }


    // to send messages by hiting enter
    var wage = document.getElementById("user-message");
    wage.addEventListener("keydown", function (e) {
        if (e.keyCode === 13) {  //checks whether the pressed key is "Enter"
            send();
        }
    });
</script>

